mapper.select:
SELECT a.com_no as comNo, a.flow_id as flowId, a.flow_no as flowNo, a.branch_no as branchNo, a.item_no as itemNo, a.source_price as sourcePrice, a.sale_price as salePrice, a.cost_price as costPrice, a.sale_qnty as saleQnty, a.sale_money as saleMoney, a.sell_way as sellWay, a.oper_id as operId, a.sale_man as saleMan, a.oper_date as operDate , a.card_id as cardId, a.card_no as cardNo, b.posid as posid FROM t_rm_saleflow a, t_sys_pos_status b where left(a.flow_no, CHAR_LENGTH(b.posid)) = b.posid
and a.oper_date >= #{date,jdbcType=TIMESTAMP}
ORDER BY a.oper_date asc limit 100
